		else
		{
			uiStencilDepth = -1f;
		}
	}

	private float MaskDepthToID()
	{
		if (uiStencilDepth >= 8f)
		{
			Debug.Log("Attempting to use a mask with depth >= 8");
			return 0f;
		}
		return Mathf.Pow(2f, uiStencilDepth) - 1f + (float)uiMaskMode;
	}
}
