		DOTween.Sequence().Append(_backCanvasGroup.DOFade(0f, _tweenDuration).SetEase(_hideAnimationCurve)).Join(_textCanvasGroup.DOFade(0f, _tweenDuration).SetEase(_hideAnimationCurve))
