	{
		if (CanChange)
		{
			VRHand componentInParent = other.gameObject.GetComponentInParent<VRHand>();
			if (!(componentInParent == null) && !(_changeSpan > 0f))
			{
				SingletonMonoBehaviour<InputHandler>.Instance.Vibrate(componentInParent.IsLeft);
