		{
			num = 0;
		}
		for (int i = num; i < _chevs.Count; i++)
		{
			RawImage rawImage = _chevs[i];
			rawImage.color = new Color(rawImage.color.r, rawImage.color.g, rawImage.color.b, _alpha);
