			return;
		}
		spline.spline = gm.GetSpline(path);
		if (spline.spline == null)
		{
			spline.spline = ((offset.x < 0f) ? gm.objectRefrences.curveLeft.GetComponent<SplineComputer>() : gm.objectRefrences.curveRight.GetComponent<SplineComputer>());
		}
	}
