	}

	public enum e_EffectGroup
	{
		Double,
		SetComplete,
		SetHit,
		LeftCascadeStart,
		LeftCascadeComplete,
		LeftCascadeBreak,
		RightCascadeStart,
		RightCascadeComplete,
		RightCascadeBreak
	}

	[Serializable]
