	public bool Cleared(float _acc, float _pow, int _casc)
	{
		UnityEngine.Debug.Log("Checking " + _acc + " & " + _pow);
		if (_acc >= reqAccuracy.x && _acc <= reqAccuracy.y && _pow >= reqPower.x && _pow <= reqPower.y && _casc >= reqCascades)
