		if (Persistence.instance.currentDifficulty == 1)
		{
			return "Normal";
		}
		if (Persistence.instance.currentDifficulty == 2)
		{
			return "Hard";
		}
		if (Persistence.instance.currentDifficulty == 3)
		{
			return "X";
		}
		UnityEngine.Debug.Log("bad difficulty");
		return "Normal";
	}

	public void UpdateRankingPreview()
