	public void DecryptDone(string url)
	{
		videoUrl = url;
		videoAreReadyToPlay = true;
	}

	private void FixedUpdate()
	{
		if (videoAreReadyToPlay)
		{
			videoAreReadyToPlay = false;
			if (!useNewUnityPlayer)
			{
				StartCoroutine(StartVideo());
