	private float t;

	private bool fiddling;

	private GUIStyle nameStyle;

	private GUIStyle nameStyle2;

	private GUIStyle buttonStyle;

	private GUIStyle scrollStyle;

	private GUIStyle boxStyle;

	private Color baseGUIColor = Color.white;

