				m_childrenGameObjects.SetAllActive(active: true);
				SetGraphActive(active: false);
				if (m_graphyManager.Background)
				{
					m_backgroundImages.SetOneActive(1);
				}
				else
				{
					m_backgroundImages.SetAllActive(active: false);
				}
