					}
					if (a.IsKeywordEnabled("_EMISSION") && !TextureBlenderFallback._compareColor(a, b, m_generatingTintedAtlasEmission, "_EmissionColor"))
					{
						return false;
					}
					return true;
				}
				return false;
			}
			return false;
		}

