		public static void Triangulate(Vector2[] points, ref int[] output)
		{
			List<int> list = new List<int>();
			int num = points.Length;
			if (num < 3)
			{
				output = new int[0];
				return;
			}
			int[] array = new int[num];
			if (Area(points, num) > 0f)
			{
				for (int i = 0; i < num; i++)
				{
					array[i] = i;
				}
			}
			else
			{
				for (int j = 0; j < num; j++)
				{
					array[j] = num - 1 - j;
				}
			}
			int num2 = num;
			int num3 = 2 * num2;
			int num4 = 0;
			int num5 = num2 - 1;
			while (num2 > 2)
			{
				if (num3-- <= 0)
				{
					if (output.Length != list.Count)
					{
						output = new int[list.Count];
					}
					list.CopyTo(output, 0);
					return;
				}
				int num6 = num5;
				if (num2 <= num6)
				{
					num6 = 0;
				}
				num5 = num6 + 1;
				if (num2 <= num5)
				{
					num5 = 0;
				}
				int num7 = num5 + 1;
				if (num2 <= num7)
				{
					num7 = 0;
				}
				if (Snip(points, num6, num5, num7, num2, array))
				{
					int item = array[num6];
					int item2 = array[num5];
					int item3 = array[num7];
					list.Add(item3);
					list.Add(item2);
					list.Add(item);
					num4++;
					int num8 = num5;
					for (int k = num5 + 1; k < num2; k++)
					{
						array[num8] = array[k];
						num8++;
					}
					num2--;
					num3 = 2 * num2;
				}
			}
			list.Reverse();
			if (output.Length != list.Count)
			{
				output = new int[list.Count];
			}
			list.CopyTo(output, 0);
		}

