		[SerializeField]
		[HideInInspector]
		private bool _transformTangents = true;

		private Vector3 lastPosition;

		private Vector3 lastScale;

		private Quaternion lastRotation;

		private Transform trs;

		public bool transformNormals
		{
			get
			{
				return _transformNormals;
			}
			set
