				}
			}
			ArrayUtility.Add(ref subscribers, input);
		}

		public void Unsubscribe(SplineUser input)
		{
			for (int i = 0; i < subscribers.Length; i++)
			{
				if (subscribers[i] == input)
				{
					ArrayUtility.RemoveAt(ref subscribers, i);
					break;
				}
			}
		}

