		{
			Update,
			FixedUpdate,
			LateUpdate
		}

		[Serializable]
		public class Channel
		{
			public enum Interpolation
			{
				Linear,
				Spherical
			}

			[SerializeField]
			internal SplinePoint[] points = new SplinePoint[0];

			[SerializeField]
