		public void FlatY()
		{
			for (int i = 0; i < buffer.pointCount; i++)
			{
				SplinePoint value = buffer.points[i];
				value.position.y = 0f;
				value.tangent.y = 0f;
				value.tangent2.y = 0f;
				value.normal = Vector3.up;
