			}
		}

		public void FlatZ()
		{
			for (int i = 0; i < buffer.pointCount; i++)
			{
				SplinePoint value = buffer.points[i];
				value.position.z = 0f;
				value.tangent.z = 0f;
				value.tangent2.z = 0f;
