		{
			base.transform.localScale = ScaleAwake;
		}
		for (int i = 0; i < OnSelectObjs.Length; i++)
		{
			OnSelectObjs[i].SetActive(value: false);
		}
		for (int j = 0; j < NormalObjs.Length; j++)
		{
			NormalObjs[j].SetActive(value: true);
		}
