	public csHorseModelController InitHorse(HorseType type, bool onPlatform = false)
	{
		if ((bool)CurHorseObj)
		{
			if (CurType == type)
			{
				curHorseCtrl = CurHorseObj.GetComponent<csHorseModelController>();
				return curHorseCtrl;
			}
			UnityEngine.Object.Destroy(CurHorseObj);
		}
		CurType = type;
