			{
				InGameMissionDataList[i].isComplet = true;
			}
		}
	}

	public int GetMissionIndexByType(csMissionType _type)
	{
		for (int i = 0; i < InGameMissionDataList.Count; i++)
		{
			if (InGameMissionDataList[i]._Type == _type)
			{
				return i;
			}
		}
		return -1;
