			{
				PropShopItemList.Find((csItemInPropShop x) => x.m_ItemType == itemtype).SetToEquipState(isE: false);
			}
			csPlayerData.UnPackAheadBuff(aheadBuff);
		}
		csPlayerData.SaveEquipBuffList();
	}

