	};

	private static Bones[] rightBones = new Bones[21]
	{
		Bones.RIGHT_WRIST,
		Bones.RIGHT_THUMB_JOINT1,
		Bones.RIGHT_THUMB_JOINT2,
		Bones.RIGHT_THUMB_JOINT3,
