	private Vector3 GetIntersectionPosition(Camera cam, RaycastResult raycastResult)
	{
		if (cam == null)
		{
			return Vector3.zero;
		}
		float num = raycastResult.distance + cam.nearClipPlane;
		return cam.transform.forward * num + cam.transform.position;
	}

