		}
		if (imeManager == null)
		{
			WVR_Log.Log.e(LOG_TAG, "isInitialized failed because fail to get imeManager object");
			return;
		}
		mCallback = cb;
		imeManager.Call("showKeyboard", javaIMEParameterFromCS(parameter), new RequestCompleteHandler());
