				JumpCD = hd.SkillData.SkillCDFactor * csGameData.Instance.SkillTypeToDataDic[HorseSkill.Jump].LevelData[horseSpecLevel].CDTime;
				list3.Add(JumpCD);
			}
			if (hd.SkillData.EffectSkillIDList.Contains(HorseSkill.Strike))
			{
