			if (GameManager.NpcPlayers != null)
			{
				NpcPlayer[] npcPlayers = GameManager.NpcPlayers;
				for (int i = 0; i < npcPlayers.Length; i++)
				{
					npcPlayers[i].HorsPosT.localPosition = playerRadius * (insideTunnel ? Vector3.down : Vector3.up);
