				CreateAchievementItem(list[i]);
				yield return new WaitForSeconds(moveTime);
				float y = IngameAchievementParent.anchoredPosition.y;
				y += m_GridLayoutGroup.cellSize.y;
				IngameAchievementParent.DOAnchorPosY(y, moveTime);
