		internal IEnumerator ToggleAction()
		{
			for (int i = 0; i < ToogleAmount; i++)
			{
				action = true;
				if (AnimState == AnimTag.Action)
				{
					OnAction.Invoke();
					SetFloatID(-1f);
					break;
				}
				yield return null;
			}
			action = false;
			if (AnimState != AnimTag.Action)
			{
				ActionID = -1;
				SetFloatID(0f);
			}
		}

		internal IEnumerator ToggleStun(float time)
		{
