			if (!animal || animal.GotoSleep == 0)
			{
				return;
			}
			if (animator.GetCurrentAnimatorStateInfo(layerIndex).tagHash == AnimTag.Idle)
			{
				animal.Tired++;
				if (animal.Tired >= animal.GotoSleep)
				{
					animal.SetIntID(transitionID);
					animal.Tired = 0;
				}
			}
			else
			{
				CyclesToSleep();
			}
