		private void CyclesToSleep()
		{
			if (CyclesFromController)
			{
				Cycles = animal.GotoSleep;
				if (Cycles == 0)
				{
					return;
				}
			}
			currentCycle++;
			if (currentCycle >= Cycles)
			{
				animal.SetIntID(transitionID);
				currentCycle = 0;
			}
