		private void OnRiderMounted()
		{
			Animator anim = Montura.ActiveRider.Anim;
			riderHand_L = anim.GetBoneTransform(HumanBodyBones.LeftHand);
			riderHand_R = anim.GetBoneTransform(HumanBodyBones.RightHand);
