			if (isInCombatMode)
			{
				Anim.speed = 1f;
				Anim.SetInteger(Hash.IDInt, -1);
				if ((bool)ActiveAbility)
				{
					if (Active_IMWeapon != null && Active_IMWeapon.Active && ActiveAbility.WeaponType() != GetWeaponType())
