		private void CalculateCameraTargetSide()
		{
			float num = 0f;
			float num2 = 0f;
			if ((bool)Target)
			{
				num = Vector3.Dot((_transform.position - Target.position).normalized, _transform.right);
			}
			if ((bool)_cam)
			{
				num2 = Vector3.Dot(_cam.transform.right, _transform.forward);
			}
			TargetSide = num > 0f;
			CameraSide = num2 > 0f;
		}

