			else
			{
				Vector3 screenPoint = ((AimDot != null) ? AimDot.position : new Vector3((float)Screen.width * 0.5f, (float)Screen.height * 0.5f));
				aimDirection = MalbersTools.DirectionFromCamera(transform, screenPoint, out aimRayHit, HitMask);
