			while (time > 0f && elapsedTime <= time)
			{
				float t2 = curve?.Evaluate(elapsedTime / time) ?? (elapsedTime / time);
				t1.rotation = Quaternion.LerpUnclamped(CurrentRot, NewRotation, t2);
				elapsedTime += Time.deltaTime;
				yield return null;
