		else if (Input.GetKeyDown(KeyCode.LeftArrow))
		{
			PrevWeapon();
		}
	}

	private void NextWeapon()
	{
		if ((int)DefaultFXType < Enum.GetNames(typeof(F3DFXType)).Length - 1)
		{
			Stop();
			DefaultFXType++;
		}
	}

	private void PrevWeapon()
	{
		if (DefaultFXType > F3DFXType.Vulcan)
		{
			Stop();
			DefaultFXType--;
		}
	}

