			timerID = F3DTime.time.AddTimer(0.05f, Vulcan);
			Vulcan();
			break;
		case F3DFXType.SoloGun:
			timerID = F3DTime.time.AddTimer(0.2f, SoloGun);
			SoloGun();
			break;
		case F3DFXType.Sniper:
