			return "";
		}
		int num = -1;
		int num2 = -1;
		num = ((typeof(int) == args[0].GetType()) ? ((int)args[0]) : ((!(typeof(string) == args[0].GetType())) ? Object2Int(args[0]) : GetColumn((string)args[0])));
