	public static string[] GetCSVStringList(string csvLine)
	{
		List<string> list = new List<string>();
		string text = "";
		bool flag = false;
		for (int i = 0; i < csvLine.Length; i++)
		{
			if (csvLine[i] == ',')
			{
				if (!flag)
				{
					list.Add(text);
					text = "";
				}
				else
				{
					text += csvLine[i];
				}
			}
			else if (csvLine[i] == '"')
			{
				if (i != csvLine.Length - 1)
				{
					if (!flag)
					{
						flag = true;
					}
					else if (i + 1 <= csvLine.Length - 1)
					{
						if (csvLine[i + 1] == '"')
						{
							text += "\"";
							i++;
						}
						else if (csvLine[i + 1] == ',')
						{
							flag = false;
						}
					}
				}
				else
				{
					list.Add(text);
					text = "";
					flag = false;
				}
			}
			else
			{
				text += csvLine[i];
			}
		}
		if (text == "")
		{
			if (csvLine.Length > 0 && csvLine[csvLine.Length - 1] == ',')
			{
				list.Add("");
			}
		}
		else
		{
			list.Add(text);
		}
		return list.ToArray();
	}

