		for (int i = 0; i < playerList.Length; i++)
		{
			num++;
			bool isLocal = playerList[i].IsLocal;
			string text2 = Regex.Unescape(LTLocalization.GetText(""));
			text = text + ((num > 1) ? "\n\n" : "") + (isLocal ? "<color=green>" : "") + Regex.Unescape(LTLocalization.GetText("")) + "  " + num + " " + (isLocal ? "</color>" : "") + text2;
