	{
		float num = 0f;
		for (int i = 0; i < Probs.Length; i++)
		{
			num += Probs[i];
		}
		float num2 = UnityEngine.Random.value * num;
		for (int j = 0; j < Probs.Length; j++)
		{
			if (num2 < Probs[j])
