		{
			ShootNpc(componentInParent);
			if (pierce)
			{
				hitList.Add(componentInParent);
			}
			else if ((bool)trail)
			{
				trail.Clear();
				trail.enabled = false;
			}
		}
		DestroyStillBullet();
	}

	private void OnCollisionEnter(Collision otherobj)
	{
