		GameObject gameObject = null;
		VRFreeLib value = null;
		if (g_staticGameData.Count > 0 && g_staticGameData.TryGetValue(prefabName, out value) && value.freeActorLib.Count > 0)
		{
			gameObject = value.freeActorLib.Dequeue();
		}
		if (gameObject == null)
