		if (component != null)
		{
			component.MyReset();
		}
		if (component is VRBulletPhysic)
		{
			(component as VRBulletPhysic).VRSetActive(active: true);
		}
		else
		{
			gameObject.SetActive(value: true);
		}
		gameObject.transform.position = pos;
		gameObject.transform.rotation = qua;
		return gameObject;
