	{
		if (exp)
		{
			expTimer -= delta;
			if (expTimer <= 0f)
			{
				expTimer = 0f;
				lit.enabled = false;
				VRGlobal.interActiveLib.Remove(this);
			}
			lit.intensity = 8f * expTimer / 0.3f;
			if (!m_animation.IsPlaying("jiutandonghua"))
