		}
		box.fontSize = 12;
		float realtimeSinceStartup = Time.realtimeSinceStartup;
		if (realtimeSinceStartup - mLastCalTime > 10f)
		{
			mLastCalTime = realtimeSinceStartup;
			mLowestFrames = 99.0;
		}
		if (fps < mLowestFrames)
		{
