			}
			UnityEngine.Object.Instantiate(playerUiPrefab).SendMessage("SetTarget", this, SendMessageOptions.RequireReceiver);
		}

		private void OnSceneLoaded(Scene scene, LoadSceneMode loadingMode)
		{
