		{
			PhotonNetwork.AutomaticallySyncScene = true;
			cachedRoomList = new Dictionary<string, RoomInfo>();
			roomListEntries = new Dictionary<string, GameObject>();
			PlayerNameInput.text = "Player " + UnityEngine.Random.Range(1000, 10000);
