		{
			SetActivePanel(SelectionPanel.name);
			foreach (GameObject value in playerListEntries.Values)
			{
				UnityEngine.Object.Destroy(value.gameObject);
			}
			playerListEntries.Clear();
			playerListEntries = null;
		}

